from fastapi.testclient import TestClient
from fastapi import FastAPI
import pytest
from backend import app
from backend.db import resume_jobdescrip_db

client = TestClient(app)

JWT_HEADER = {"Authorization": "Bearer valid_jwt"}
VALID_DESCRIPTION = "THIS IS A VALID DESCRIPTION"
INVALID_DESCRIPTION = "THIS IS AN INVALID DESCRIPTION "


def test_job_description_upload_valid():
    """
    Tests job_description endpoint by sending a valid description
    """
    res = client.post(
        "/api/job-description",
        json={"job_description": VALID_DESCRIPTION},
        headers=JWT_HEADER,
    )
    assert res.status_code == 200
    assert res.json() == {"message": "Job description submitted successfully."}
    assert resume_jobdescrip_db["valid_jwt"]["job_description"] == VALID_DESCRIPTION


def test_job_description_upload_invalid():
    """
    Tests job_description endpoint by sending a invalid description
    """
    res = client.post(
        "/api/job-description",
        json={"job_description": INVALID_DESCRIPTION},
        headers=JWT_HEADER,
    )
    assert res.status_code == 400
    assert res.json() == {"detail": "Job description exceeds character limit."}
